historyItem.sql:
begin

update safe_kiosk_print
set printed_date_time = sysdate
where booking_reference = :booking_reference
and collection_point_code = :collection_point_code
and job_number = :job_number;

update ca_booked_person
set printed_date_time = sysdate
where booking_reference = :booking_reference
and collection_point_code = :collection_point_code;

:success := 'true';

end;
